import LayoutBuilder from "../builders";
export default (function () {
  var scheme = LayoutBuilder();
  scheme.configureHeader(function (builder) {
    builder.registerConfig("xs", {
      position: "relative",
      initialHeight: 56
    }).registerConfig("md", {
      position: "sticky"
    });
  });
  scheme.configureEdgeSidebar(function (builder) {
    builder.create("primarySidebar", {
      anchor: "left"
    }).registerTemporaryConfig("xs", {
      width: 256
    }).registerPersistentConfig("md", {
      width: 256,
      collapsible: true,
      collapsedWidth: 64,
      persistentBehavior: "fit"
    });
  });
  scheme.enableAutoCollapse("primarySidebar", "md");
  return scheme;
});